/**
 * If you use roslib in a browser, all the classes will be exported to a global variable called ROSLIB.
 *
 * If you use nodejs, this is the variable you get when you require('roslib')
 */
var ROSLIB = this.ROSLIB || {
  REVISION : '1.3.0'
};

var assign = require('object-assign');

// Add core components
assign(ROSLIB, require('roslib/src/core'));

assign(ROSLIB, require('roslib/src/actionlib'));

assign(ROSLIB, require('roslib/src/math'));

assign(ROSLIB, require('roslib/src/tf'));

assign(ROSLIB, require('roslib/src/urdf'));

module.exports = ROSLIB;
